package br.com.senaisp.aula02;

public class ExerciciosAula02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Primeira Expressão
		double dblConta= 2.00 / 5.00;
		System.out.println("Resultado da primeira expresão é: " + dblConta);
		
		//Segunda Expressão
		dblConta= 2.00 / 3.00 + 10;
		System.out.println("Resultado da segunda expresão é: " + dblConta);

		//Terceira Expressão
		dblConta= 1.00 / 5.00 * 3.00 / 4.00 + 5;
		System.out.println("Resultado da terceira expresão é: " + dblConta);
		
		
	}

}
