public class Aula07Exe02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Desenvolver um aplicativo para mostrar os números de 1 à 100 utilizando laço For.
				System.out.println("Mostrar números de 100 a 1");
				for (int intValor = 100; intValor > 0; intValor--)
					System.out.println("" + intValor);
			}
		}
